<?php

	error_reporting(0);

	echo '<div style="width: 300px;height:100px;"><img src="data:image/gif;base64,"></div>';

	function check($boolean, $message)
	{
		echo '<div style="margin: 25px 0 0 25px;font:14px/1em Tahoma">';
		echo $boolean ? '<span style="padding-right:5px;font-weight:bold;color:green;">[OK]</span>  ' . $message : '<span style="padding-right:5px;font-weight:bold;color:red;">[FAIL]</span>  ' . $message;
		echo '</div>';
	}

	check(version_compare(phpversion(), '5.2.4', '>='), 'PHP version is at least 5.2.4');
	check(ini_get('allow_url_fopen'), 'php.ini has allow_url_fopen set to on');

	if (in_array('mbstring', get_loaded_extensions()))
		check(true, 'Module mbstring is installed');
	else
		check(false, 'Module mbstring is installed');

	if (in_array('curl', get_loaded_extensions()))
		check(true, 'Module curl is installed');
	else
		check(false, 'Module curl is installed');

	if (in_array('mod_rewrite', apache_get_modules()))
		check(true, 'Module mod_rewrite is installed');
	else
		check(false, 'Module mod_rewrite is installed');