<?php	#read site_list	$site_list_file_name = "site_list.txt";	if (file_exists($site_list_file_name) and filesize($site_list_file_name) > 0) {		$site_list = file($site_list_file_name, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);	}		$file_list_file_name = "file_list.txt";	if (file_exists($file_list_file_name) and filesize($file_list_file_name) > 0) {		$file_list = file($file_list_file_name, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);	}		for($i = 0; $i < count($file_list); $i++){		#coping files from upload to site folder		if(isset($file_list[$i])){			$file_source = "upload/".trim($file_list[$i]);			echo "file_source: $file_source\r\n";			for($j = 0; $j < count($site_list); $j++){				if(isset($site_list[$j])){					$file_dest= trim($site_list[$j])."/".trim($file_list[$i]);					echo "file_dest: $file_dest\r\n";					if (!copy($file_source, $file_dest)) {						echo "Не удалось скопировать $file_source в $file_dest\r\n";					}				}			}		}	}?>